/**
 * @description 初始化项目目录结构，以生成bjs.config.js为唯一校验，若存在该配置文件，则不执行初始脚本
 *
 * @author pakinguo
 *
 */

function init(ctype) {
	var FS = require('fs');
	var PATH = require('path');
	var colors = require('colors');
	var UTIL = require('bjs-util');

	var cwd = process.cwd();
	UTIL.timer.start();

	if (FS.existsSync(PATH.join(cwd, 'bjs.conf.js'))) {
		process.stdout.write('bjs.conf.js'.green + ' is already existed!\n');
		exit;
	}

	switch (ctype) {
		case 'cmd': initCmd();break;
		case 'amd': break;
		case 'none': break;
		default: break;
	}


	function initCmd(){
		console.log(PATH.join(__dirname__, 'lib/init-cmd.js'))
	}
}

module.exports = init;