var chokidar = require('chokidar');

var watch = chokidar.watch(process.argv[2], {
	ignored: /[\/\\]\./
});


watch.on('add', function(path) { console.log('Add File: ' + path) } )
	 .on('addDir', function(path) { console.log('AddDir File: ' + path) } )
	 .on('change', function(path) { console.log('Change File: ' + path) } )
	 .on('unlink', function(path) { console.log('Unlink File: ' + path) } )
	 .on('unlinkDir', function(path) { console.log('UnlinkDir File: ' + path) } )