/**
 * 
 * 
 */

'use strict';

var bjs = {};


/**
 * 路由
 * @return {[type]} [description]
 */
bjs.run = function(){
	var argvs = process.argv;
	switch (argvs[2]){
		case 'init': require('bjs-init/init.js')(argvs[3]);break;
	}
}


/**
 * 配置
 * @param {string} key 配置项键值
 * @param {string||object} value 配置项的值
 */
bjs.set = function(key, value){
	if(!(key in bjs.config)){
		return;
	}

	bjs.config[key] = value;
}
// 配置项
bjs.config = {

	// 项目采用的模块加载类型，仅支持cmd、amd、none（目前仅完成cmd）
	ctype: 'cmd',

	// 项目源路径
	src: './src',

	// 默认部署的路径
	dest: '../resource',

	// 开启手动打包
	pkg: false,

	// 需要排除打包的模块映射（如果开启手动打包，则手动打包的配置会忽略此项）
	pkgExclude: {},

	// css编译器
	csscompiler: false,

	// css sprite
	csssprite: false,

	// i18n
	i18n: false

}

module.exports = bjs;